import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
ChartJS.register(ArcElement, Tooltip, Legend);

function DashboardPage() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    axios.get('/api/items')
      .then(res => {
        if (Array.isArray(res.data)) {
          setItems(res.data);
        } else {
          setItems([]);
        }
      })
      .catch(() => setItems([]));
  }, []);

  const categoryCounts = items.reduce((acc, item) => {
    acc[item.category] = (acc[item.category] || 0) + 1;
    return acc;
  }, {});

  const pieData = {
    labels: Object.keys(categoryCounts),
    datasets: [
      {
        label: '# of Items by Category',
        data: Object.values(categoryCounts),
        backgroundColor: [
          '#6c63ff',
          '#00c9a7',
          '#ff6f61',
          '#ffd166',
          '#72A0C1',
          '#8B94A3',
        ],
        borderWidth: 1,
      },
    ],
  };

  return (
    <main className="container">
      <h2>📊 Dashboard</h2>
      <div className="dashboard">
        <div className="stat-box">📦 Total Items: {items.length}</div>
        <div className="stat-box">📂 Categories: {new Set(items.map(i => i.category)).size}</div>
        <div className="stat-box">⚠️ Low Stock: {items.filter(i => i.quantity < 5).length}</div>
        <div className="stat-box">💰 Total Value: ${items.reduce((acc, item) => acc + item.quantity * item.price, 0).toFixed(2)}</div>
      </div>

      <div style={{ maxWidth: '250px', margin: '2rem auto' }}>
        <Pie data={pieData} />
      </div>
    </main>
  );
}

export default DashboardPage;
