import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';

function Navbar({ toggleTheme, darkMode }) {
  const location = useLocation();

  return (
    <nav className="navbar">
      <div className="nav-title">Inventory Manager</div>
      <div className="nav-links">
        <Link to="/" className={`nav-btn ${location.pathname === '/' ? 'active' : ''}`}>üè† Home</Link>
        <Link to="/dashboard" className={`nav-btn ${location.pathname === '/dashboard' ? 'active' : ''}`}>üìä Dashboard</Link>
        <Link to="/inventory" className={`nav-btn ${location.pathname === '/inventory' ? 'active' : ''}`}>üìã My Inventory</Link>
        <button className="theme-toggle" onClick={toggleTheme}>
          {darkMode ? 'Light Mode' : 'Dark Mode'}
        </button>
      </div>
    </nav>
  );
}

export default Navbar;
