import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';
import HomePage from './HomePage';
import DashboardPage from './DashboardPage';
import './styles.css';

function App() {
  const location = useLocation();
  const [darkMode, setDarkMode] = useState(true);

  // Set initial theme class on mount
  useEffect(() => {
    document.body.classList.add('dark');
  }, []);

  const toggleTheme = () => {
    const isCurrentlyDark = document.body.classList.contains('dark');
    document.body.classList.toggle('dark');
    document.body.classList.toggle('light');
    setDarkMode(!isCurrentlyDark); // track the actual theme
  };

  return (
    <div>
      <nav className="navbar">
        <div className="nav-title">Inventory Manager</div>
        <div className="nav-links">
          <Link to="/" className="nav-btn">üè† Home</Link>
          <Link to="/dashboard" className="nav-btn">üìä Dashboard</Link>
          <button className="theme-toggle" onClick={toggleTheme}>
            {darkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
        </div>
      </nav>

      <Routes location={location} key={location.pathname}>
        <Route path="/" element={<HomePage />} />
        <Route path="/dashboard" element={<DashboardPage />} />
      </Routes>
    </div>
  );
}

export default App;
