import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

function DashboardPage() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    axios.get('/api/items')
      .then(res => {
        if (Array.isArray(res.data)) {
          setItems(res.data);
        } else {
          setItems([]);
        }
      })
      .catch(() => setItems([]));
  }, []);

  const totalItems = items.length;
  const categories = [...new Set(items.map(item => item.category))];
  const lowStock = items.filter(item => item.quantity < 5);
  const totalValue = items.reduce((acc, item) => acc + item.quantity * item.price, 0).toFixed(2);

  // ✅ Step 1: Define ALL categories
  const ALL_CATEGORIES = [
    'Fruits', 'Vegetables', 'Food', 'Shoes', 'Stationery',
    'Electronics', 'Books', 'Clothing', 'Toys', 'Home'
  ];

  // ✅ Step 2: Create data counts for each
  const categoryCounts = ALL_CATEGORIES.map(cat =>
    items.filter(item => item.category.toLowerCase() === cat.toLowerCase()).length
  );

  // ✅ Step 3: Pie chart data setup
  const categoryData = {
    labels: ALL_CATEGORIES,
    datasets: [
      {
        label: 'Category Distribution',
        data: categoryCounts,
        backgroundColor: [
          '#6c63ff', '#00c9a7', '#ff6b6b', '#ffa500', '#2d98da',
          '#9b59b6', '#3498db', '#e67e22', '#2ecc71', '#34495e'
        ],
        borderWidth: 1
      }
    ]
  };

  const mostStocked = [...items].sort((a, b) => b.quantity - a.quantity).slice(0, 3);

  return (
    <main className="container">
      <h2>📊 Inventory Overview</h2>

      <div className="dashboard">
        <div className="stat-box">📦 Total Items: {totalItems}</div>
        <div className="stat-box">📂 Categories: {categories.length}</div>
        <div className="stat-box">⚠️ Low Stock: {lowStock.length}</div>
        <div className="stat-box">💰 Inventory Value: ${totalValue}</div>
      </div>

      <div className="dashboard-section">
        <h3>📊 Category Breakdown</h3>
        <div style={{ maxWidth: '400px', margin: '0 auto' }}>
          <Pie data={categoryData} />
        </div>
      </div>

      <div className="dashboard-section">
        <h3>🏆 Top 3 Stocked Items</h3>
        <ul>
          {mostStocked.map((item, index) => (
            <li key={item._id}>{['🥇', '🥈', '🥉'][index]} {item.name} (Qty: {item.quantity})</li>
          ))}
        </ul>
      </div>

      <div className="dashboard-section">
        <h3>⚠️ Low Stock Items</h3>
        <ul>
          {lowStock.map(item => (
            <li key={item._id}>{item.name} (Qty: {item.quantity})</li>
          ))}
        </ul>
      </div>
    </main>
  );
}

export default DashboardPage;
