import React, { useEffect, useState } from 'react';
import axios from 'axios';

function InventoryPage() {
  const [items, setItems] = useState([]);
  const [editingItem, setEditingItem] = useState(null);
  const [form, setForm] = useState({ name: '', quantity: '', price: '', category: '', tags: '' });

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios.get('/api/items')
      .then(res => setItems(Array.isArray(res.data) ? res.data : []))
      .catch(() => setItems([]));
  };

  const handleEdit = item => {
    setEditingItem(item._id);
    setForm({
      name: item.name,
      quantity: item.quantity,
      price: item.price,
      category: item.category,
      tags: item.tags.join('; '),
    });
  };

  const handleDelete = async id => {
    await axios.delete(`/api/items/${id}`);
    fetchItems();
  };

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async e => {
    e.preventDefault();
    const tagsArray = form.tags.split(';').map(tag => tag.trim()).filter(Boolean);
    const payload = { ...form, tags: tagsArray };
    await axios.put(`/api/items/${editingItem}`, payload);
    setEditingItem(null);
    setForm({ name: '', quantity: '', price: '', category: '', tags: '' });
    fetchItems();
  };

  return (
    <main className="container">
      <h2>📋 My Inventory</h2>

      {editingItem && (
        <form onSubmit={handleUpdate} className="form">
          <input name="name" value={form.name} onChange={handleChange} placeholder="Item Name" required />
          <input name="quantity" type="number" value={form.quantity} onChange={handleChange} placeholder="Quantity" required />
          <input name="price" type="number" value={form.price} onChange={handleChange} placeholder="Price" required />
          <input name="category" value={form.category} onChange={handleChange} placeholder="Category" required />
          <input name="tags" value={form.tags} onChange={handleChange} placeholder="Tags (e.g. tech;blue)" />
          <button type="submit">✅ Update</button>
        </form>
      )}

      <ul className="item-list">
        {items.map(item => (
          <li key={item._id} className="item">
            <strong>{item.name}</strong> – Qty: {item.quantity} – ${item.price.toFixed(2)}<br />
            <em>Category:</em> {item.category} <br />
            <em>Tags:</em> {item.tags.join(', ')}
            <div className="actions">
              <button onClick={() => handleEdit(item)}>✏️ Edit</button>
              <button onClick={() => handleDelete(item._id)}>🗑️ Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </main>
  );
}

export default InventoryPage;
