import React, { useEffect, useState } from 'react';
import axios from 'axios';

function InventoryPage() {
  const [items, setItems] = useState([]);
  const [search, setSearch] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const fetchItems = () => {
    axios.get('/api/items')
      .then(res => setItems(Array.isArray(res.data) ? res.data : []))
      .catch(() => setItems([]));
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/items/${id}`);
      fetchItems();
    } catch (err) {
      console.error('Error deleting item:', err);
    }
  };

  const handleEdit = (item) => {
    localStorage.setItem('editItem', JSON.stringify(item));
    window.location.href = '/';
  };

  const categories = ['All', ...new Set(items.map(item => item.category))];

  const filteredItems = items
    .filter(item => item.name.toLowerCase().includes(search.toLowerCase()))
    .filter(item => selectedCategory === 'All' ? true : item.category === selectedCategory);

  return (
    <main className="container">
      <h2>My Inventory</h2>

      <input
        className="search"
        type="text"
        placeholder="Search items..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ marginBottom: '1rem' }}
      />

      <div className="category-filter">
        <label htmlFor="category">Sort by Category:</label>
        <select
          id="category"
          value={selectedCategory}
          onChange={e => setSelectedCategory(e.target.value)}
        >
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      <ul className="item-list">
        {filteredItems.map(item => (
          <li key={item._id} className="item">
            <strong>{item.name}</strong> ‚Äì Qty: {item.quantity} ‚Äì ${item.price.toFixed(2)}<br />
            <em>Category:</em> {item.category}<br />
            <em>Tags:</em> {item.tags.join(', ')}
            <div className="actions">
              <button onClick={() => handleEdit(item)}>‚úèÔ∏è</button>
              <button onClick={() => handleDelete(item._id)}>üóëÔ∏è</button>
            </div>
          </li>
        ))}
      </ul>
    </main>
  );
}

export default InventoryPage;
